import React, { Component } from 'react';

import './App.css';

class App extends Component {

componentDidMout(){
  this.showMap()
}

showMap = () => {
  addJsScript("https://maps.googleapis.com/maps/api/js?key=AIzaSyAqOATs_F6ts1BC-cjUBcnJGHYO3JCpZt4&callback=initMap")
  window.initMap = this.initMap
}

//https://developers.google.com/maps/documentation/javascript/tutorial
  initMap = () => {
      var map = new window.google.maps.Map(document.getElementById('map'), {
          center: {lat: -34.397, lng: 150.644},
          zoom: 8
        });
      }

    render() {
        return ( 
          <main>
          <div id="map"></div>
          </main>
        )
    }
}

//call the javascript lib
function addJsScript(url) {
  var firstItem = window.document.getElementByTagName("script")[0]
  var secondItem = window.document.createElement("script")
  secondItem.src= url
  secondItem.async = true
  secondItem.defer = true
  firstItem.parentNode.insertBefore(secondItem, firstItem)
}

export default App;
